import { isPresent } from "../utils";
/**
 * @hidden
 */
export var getGroupRowArgs = function (groupItem) {
    if (!isPresent(groupItem)) {
        return null;
    }
    return { group: groupItem.data, groupIndex: groupItem.index, parentGroup: getGroupRowArgs(groupItem.parentGroup) };
};
